// Função para contar artigos por categoria
function countArticlesByCategory($articles, $category_slug)
{
    $count = 0;
    foreach ($articles as $article) {
        if ($article['category_slug'] === $category_slug) {
            $count++;
        }
    }
    return $count;
}

// Função para filtrar artigos por categoria
function getArticlesByCategory($articles, $category_slug)
{
    $filtered = [];
    foreach ($articles as $article) {
        if ($article['category_slug'] === $category_slug) {
            $filtered[] = $article;
        }
    }
    return $filtered;
}
